import nltk
from nltk.stem import PorterStemmer
from nltk.corpus import wordnet
nltk.download('wordnet')

ps = PorterStemmer()


def get_stemmed_tokens(sentence):
    return [ps.stem(word) for word in sentence]


def get_synonyms(word):
    synonyms = set()
    for syn in wordnet.synsets(word):
        for lemma in syn.lemmas():
            synonyms.add(lemma.name())
    return synonyms


def align_words_func(hypothesis, reference):
    hypothesis_stemmed = get_stemmed_tokens(hypothesis)
    reference_stemmed = get_stemmed_tokens(reference)

    matched_hypothesis = set()
    matched_reference = set()

    matches = []

    # Match exact words and stems
    for i, h_word in enumerate(hypothesis_stemmed):
        for j, r_word in enumerate(reference_stemmed):
            if i not in matched_hypothesis and j not in matched_reference:
                if h_word == r_word:
                    matches.append((hypothesis[i], reference[j]))
                    matched_hypothesis.add(i)
                    matched_reference.add(j)

    # Match synonyms if not already matched
    for i, h_word in enumerate(hypothesis):
        if i not in matched_hypothesis:
            h_synonyms = get_synonyms(h_word)
            for j, r_word in enumerate(reference):
                if j not in matched_reference and r_word in h_synonyms:
                    matches.append((h_word, r_word))
                    matched_hypothesis.add(i)
                    matched_reference.add(j)

    unmatched_hypothesis = [hypothesis[i] for i in range(len(hypothesis)) if i not in matched_hypothesis]
    unmatched_reference = [reference[i] for i in range(len(reference)) if i not in matched_reference]

    return matches, unmatched_hypothesis, unmatched_reference
