import numpy as np
from scipy.stats import hmean

def count_unique_hmean(data: np.ndarray) -> int:
    unique_hmeans = set()
    for row in data:
        try:
            hm = hmean(row)
            if np.isnan(hm):
                unique_hmeans.add(float('nan'))
            else:
                unique_hmeans.add(hm)
        except ValueError:  # Handle row with NaNs which leads to domain error
            unique_hmeans.add(float('nan'))
    return len(unique_hmeans)
