def compute_shear(E: np.ndarray, factor: int, axis: int) -> np.ndarray:
    if axis == 0:  # Shearing along the vertical axis (rows)
        for i in range(E.shape[0]):
            shift = factor * i
            E[i] = np.roll(E[i], shift)
    elif axis == 1:  # Shearing along the horizontal axis (columns)
        for j in range(E.shape[1]):
            shift = factor * j
            E[:, j] = np.roll(E[:, j], shift)
    return E
