def compute_shear(E: np.ndarray, factor: int, axis: int) -> np.ndarray:
    if axis == 0:  # Shearing along the vertical axis (rows are shifted)
        for i in range(E.shape[0]):
            E[i] = np.roll(E[i], factor * i)
    elif axis == 1:  # Shearing along the horizontal axis (columns are shifted)
        for j in range(E.shape[1]):
            E[:, j] = np.roll(E[:, j], factor * j)
    else:
        raise ValueError("Axis must be 0 or 1.")
    return E
